#include "camera_custom_fsc.h"
#include <stdlib.h>  // For atio().
#include <cutils/properties.h>  // For property_get().

#define CUS_FSC_SUPPORT                     1
#define CUS_FSC_DEBUG_LEVEL                 0
#define CUS_FSC_MARCO_INF_RATIO_OFFSET      0 // unit 10000 = RATIO 1.0     OFFSET 1000 = RATIO 0.1 x 10000
#define CUS_FSC_MAX_CROP_RATIO              550 // 1000 = 10% = 0.1 * 10000
#define CUS_FSC_AF_DAMP_TIME_OFFSET         (-8000) // unit us:1000 = 1ms , reasonable value: 0 > CUS_FSC_AF_DAMP_TIME_OFFSET > -12000

MBOOL FSCCustom::isSupportFSC()
{
    MBOOL ret = false;

    if (::property_get_int32(FSC_FSC_ENABLE_PROPERTY, CUS_FSC_SUPPORT))
        ret = true;

    return ret;
}

MBOOL FSCCustom::isEnabledFSC(MUINT32 mask)
{
    MBOOL ret = false;

    // don't enable FSC in dual-cam, PIP scenario, and high speed mode
    if (isSupportFSC() && !isFSCUsageMaskEnable(mask, USAGE_MASK_DUAL_ZOOM) &&
        !isFSCUsageMaskEnable(mask, USAGE_MASK_MULTIUSER) && !isFSCUsageMaskEnable(mask, USAGE_MASK_HIGHSPEED))
        ret = true;

    return ret;
}

MINT32 FSCCustom::getDebugLevel()
{
    return ::property_get_int32(FSC_DEBUG_LEVEL_PROPERTY, CUS_FSC_DEBUG_LEVEL);
}

MINT32 FSCCustom::getMarcoToInfRatioOffset()
{
    return ::property_get_int32(FSC_MARCO_INF_RATIO_OFFSET_PROPERTY, CUS_FSC_MARCO_INF_RATIO_OFFSET);
}

MINT32 FSCCustom::getMaxCropRatio()
{
    return ::property_get_int32(FSC_MAX_CROP_RATIO_PROPERTY, CUS_FSC_MAX_CROP_RATIO);
}

MINT32 FSCCustom::getAfDampTimeOffset()
{
    return ::property_get_int32(FSC_AF_DAMPTIME_OFFSET_PROPERTY, CUS_FSC_AF_DAMP_TIME_OFFSET);
}



