#ifndef _DEV_KDF_PUB_IF_H_
#define _DEV_KDF_PUB_IF_H_

/*
     master key generation by per-device key and input data with aes 128 e ecb

     input:
       pui1_inp - input data buffer
       ui4_sz   - size of pui1_inp/pui1_key, multiple of 16 bytes

     output:
       pui1_key - master key buffer

     return:
            0  - success
           <0  - fail

*/
extern int32_t dev_kdf_gen_master
(
uint8_t* pui1_inp,
uint8_t* pui1_key,
uint32_t ui4_sz
);

#endif /* _DEV_KDF_PUB_IF_H_ */