#ifndef _AWB_CONFIG_H_
#define _AWB_CONFIG_H_
#include <isp_config/isp_awb_config.h>

/* AWB HW configure*/
/*
typedef struct
{
	AWB_GAIN_T	rIspAWBPreGain1; // 1x = 512
    MUINT32 u4AAOmode;
	MUINT32 imgW;
	MUINT32 imgH;
	MUINT32 blkW;
	MUINT32 blkH;

} AWB_CONFIG_INFO_T;
*/

typedef class ISP_AWB_CONFIG
{
    typedef ISP_AWB_CONFIG    MyType;
private:

protected:
    ISP_AWB_CONFIG(MINT32 const eSensorDev)
    {}

    virtual ~ISP_AWB_CONFIG() {}

public:
    static MyType&  getInstance(MINT32 const eSensorDev);

public: //Interfaces
   
    MVOID AWBConfig(AWB_STAT_CONFIG_T *a_sAWBConfig, AWBResultConfig_T *p_sAWBRegInfo);
    MVOID AWBConfig_update(AWB_STAT_CONFIG_T *a_sAWBConfig, AWBResultConfig_T *p_sAWBRegInfo);

} ISP_AWB_CONFIG_T;

template <MINT32 const eSensorDev>
class ISP_AWB_CONFIG_DEV : public ISP_AWB_CONFIG_T
{
public:
    static
    ISP_AWB_CONFIG_T&
    getInstance()
    {
        static ISP_AWB_CONFIG_DEV<eSensorDev> singleton;
        return singleton;
    }
    virtual MVOID destroyInstance() {}

    ISP_AWB_CONFIG_DEV()
        : ISP_AWB_CONFIG_T(eSensorDev)
    {}

    virtual ~ISP_AWB_CONFIG_DEV() {}

};

#endif // _AWB_CONFIG_H_
