#ifndef _FAKE_ISP_CLK_H_
#define _FAKE_ISP_CLK_H_

#ifndef MFALSE
#define MFALSE 0
#endif
#ifndef MTRUE
#define MTRUE 1
#endif
#ifndef MUINT8
typedef unsigned char MUINT8;
#endif

#ifndef MUINT32
typedef unsigned int MUINT32;
#endif
#ifndef MINT32
typedef int MINT32;
#endif
#ifndef MBOOL
typedef int MBOOL;
#endif

#ifndef MVOID
typedef void MVOID;
#endif

enum FAKE_CLK_LEVEL{
    FCLK_LEVEL_L = 0,
    FCLK_LEVEL_M,
    FCLK_LEVEL_H,

    MAX_FCLK_LEVEL
};

class FAKE_CLK{
public:
    FAKE_CLK();
public:

    typedef enum{
        pix_1 = 0,
        pix_2 = 1,
        pix_4 = 2
    }E_pixmode;


    MBOOL   Init_Info(MUINT32 fps,MUINT32 Width,MUINT32 Height,E_pixmode pix);
    MBOOL   Start_Cfg(MVOID);
    MUINT32 Get_Fake_Clk(enum FAKE_CLK_LEVEL level);
private:
    MUINT32     m_fps;
    MUINT32     m_TG_W;
    MUINT32     m_TG_H;
    E_pixmode   m_pix;
};

#endif
