#ifndef _HWSYNC_DRV_IMP_PARA_H_
#define _HWSYNC_DRV_IMP_PARA_H_
#include <mtkcam/drv/IHwSyncDrv.h>
//-----------------------------------------------------------------------------
#define SMI_CLOCK(x)    ({\
    MUINT32 clk=1;\
    if(x == E_SMI_LOW){\
        clk = 227000000;\
    }\
    else if(x == E_SMI_HIGH){\
        clk = 436000000;\
    }\
    clk;\
})

#define REF_CLOCK(x)    (SMI_CLOCK(x)/100000)
#define DIFF_THRECNT(x) (SMI_CLOCK(x)/1000)   //1000us -> 1000*smi/1000000
#define DIFF_THRECNT_NEW(x) (SMI_CLOCK(x)/1000/2)   //500us // TODO: check with user about this value!
#define CVGTHRE_VDDIFF  (500)   //us
#define CVGTHRE_FPSDIFF (500)   //us

#define SYNC_TYPE (HW_SYNC_TYPE_VER_2)
//-----------------------------------------------------------------------------
#endif


