/* Portable data types. */
#include "NatureDSP_types.h"
/* Signal Processing Library API. */
#include "NatureDSP_Signal.h"
/* Common utility and macros declarations. */
#include "common.h"
/* Filter instance structure. */
#include "bqriir32x32_df2_common.h"

#define AE_PKSRF32_( st, acc, offs ) \
  {                                  \
    ae_f32x2 st_ = st;               \
    AE_PKSRF32( st_, acc, offs );    \
    st = st_;                        \
  }

/*-------------------------------------------------------------------------
  Biquad Real IIR
  Computes a IIR filter (cascaded IIR direct form I or II using 5 
  coefficients per biquad + gain term). Real input data are stored
  in vector x. The filter output result is stored in vector r. The filter 
  calculates N output samples using SOS and G matrices.
  NOTES:
  1. Biquad coefficients may be derived from standard SOS and G matrices 
  generated by MATLAB. However, typically biquad stages have big peaks 
  in their step response which may cause undesirable overflows at the 
  intermediate outputs. To avoid that the additional scale factors 
  coef_g[M] may be applied. These per-section scale factors may require 
  some tuning to find a compromise between quantization noise and possible 
  overflows. Output of the last section is directed to an additional 
  multiplier, with the gain factor being a power of two, either negative 
  or non-negative. It is specified through the gain parameter of filter
  initialization function.

  Precision: 
  32x16 32-bit data, 16-bit coefficients, 32-bit intermediate stage outputs
  32x32 32-bit data, 32-bit coefficients, 32-bit intermediate stage outputs 
  f     floating point (DF I, DF II and DF IIt)

  Input:
  N             length of input sample block
  M             number of biquad sections
  coef_sos[M*5] filter coefficients stored in blocks of 5 numbers: 
                b0 b1 b2 a1 a2. 
                For fixed-point funcions, fixed point format of filter 
                coefficients is Q1.14 for 32x16, or Q1.30 for 32x16 and 
                32x32 
  coef_g[M]     scale factor for each section, Q15 (for fixed-point 
                functions only). 
  gain          total gain shift amount applied to output signal of the
                last section, -48..15
  x[N]          input samples, Q31 or floating point 
  Output:
  r[N]          output data, Q31 or floating point 

  Restriction:
  x,r,coef_g,coef_sos  must not overlap
  x,r                  must be aligned on 8-byte boundary 
  N                    must be a multiple of 2
-------------------------------------------------------------------------*/
void bqriir32x32_df2_process( bqriir32x32_df2_handle_t _bqriir,
                      int32_t * restrict       r,
                const int32_t *                x, int N )
{
  bqriir32x32_df2_ptr_t bqriir = (bqriir32x32_df2_ptr_t)_bqriir;

  const ae_int32x2*          coef_gsos;
        ae_int32x2* restrict state;
  const ae_int32x2* restrict px;
        ae_int32x2* restrict pr;

  int M;
  int m, n;

  NASSERT( bqriir && bqriir->magic == MAGIC && r && x );

  NASSERT_ALIGN8( x );
  NASSERT_ALIGN8( r );

  M = bqriir->M;
  NASSERT( N%2==0 );
  coef_gsos = (const ae_int32x2*)bqriir->coef_gsos;
  state     = (      ae_int32x2*)bqriir->state;

  px = (const ae_int32x2*)x;
  pr = (      ae_int32x2*)r;

  //
  // Perform data block processing for each pair of successive sections. Use the
  // output array r[N] for temporal storage of inter-section signal.
  //

  for ( m=0; m<M/2; m++ )
  {
    ae_int32x2 cf0_gb2, cf0_0b0, cf0_b2b1, cf0_a2a1;
    ae_int32x2 cf1_gb2, cf1_0b0, cf1_b2b1, cf1_a2a1;

    ae_int32x2 st0, st1;
    ae_int32x2 x01, y01;

    ae_int64 fb00, fb01, ff00, ff01; // fbXY == Feedback, ffXY == Feedforward
    ae_int64 fb10, fb11, ff10, ff11; // X - section number, Y - sample number

    //
    // Load coefficients for the m-th pair of sections.
    //

    AE_L32X2_IP( cf0_gb2 , coef_gsos, +2*4 );
    AE_L32X2_IP( cf0_0b0 , coef_gsos, +2*4 );
    AE_L32X2_IP( cf0_b2b1, coef_gsos, +2*4 );
    AE_L32X2_IP( cf0_a2a1, coef_gsos, +2*4 );
                                       
    AE_L32X2_IP( cf1_gb2 , coef_gsos, +2*4 );
    AE_L32X2_IP( cf1_0b0 , coef_gsos, +2*4 );
    AE_L32X2_IP( cf1_b2b1, coef_gsos, +2*4 );
    AE_L32X2_IP( cf1_a2a1, coef_gsos, +2*4 );

    //
    // Load sections' state.
    //

    st0 = AE_L32X2_I( state, 0*2*4 );
    st1 = AE_L32X2_I( state, 1*2*4 );

    //
    // Pass N/2 sample pairs through 2 sections.
    //

    __Pragma( "loop_count min=1" );
    for ( n=0; n<N/2; n++ )
    {
      AE_L32X2_IP( x01, px, +2*4 );

      //
      // Section 0, samples 0 and 1.
      //

      // Q61 <- Q30*Q31
      fb00 = AE_MUL32_HH( cf0_gb2, x01 );
      AE_MULSSD32_HH_LL( fb00, cf0_a2a1, st0 );

      ff00 = AE_MULZAAD32_HH_LL( cf0_b2b1, st0 );

      // Q31 <- Q61 + 2 - 32 w/ rounding, saturation
      AE_PKSRF32_( st0, fb00, 2 );

      AE_MULA32_LL( ff00, cf0_0b0, st0 );

      fb01 = AE_MUL32_HL( cf0_gb2, x01 );
      AE_MULSSD32_HH_LL( fb01, cf0_a2a1, st0 );

      ff01 = AE_MULZAAD32_HH_LL( cf0_b2b1, st0 );

      AE_PKSRF32_( st0, fb01, 2 );

      AE_MULA32_LL( ff01, cf0_0b0, st0 );

      // Q31 <- Q61 + 2 - 32 w/ saturation
      y01 = AE_TRUNCA32X2F64S( ff00, ff01, 2 );

      //
      // Section 1, samples 0 and 1.
      //

      fb10 = AE_MUL32_HH( cf1_gb2, y01 );
      AE_MULSSD32_HH_LL( fb10, cf1_a2a1, st1 );

      ff10 = AE_MULZAAD32_HH_LL( cf1_b2b1, st1 );

      // Q31 <- Q61 + 2 - 32 w/ rounding, saturation
      AE_PKSRF32_( st1, fb10, 2 );

      AE_MULA32_LL( ff10, cf1_0b0, st1 );

      fb11 = AE_MUL32_HL( cf1_gb2, y01 );
      AE_MULSSD32_HH_LL( fb11, cf1_a2a1, st1 );

      ff11 = AE_MULZAAD32_HH_LL( cf1_b2b1, st1 );

      AE_PKSRF32_( st1, fb11, 2 );

      AE_MULA32_LL( ff11, cf1_0b0, st1 );

      // Q47 <- Q61 - 14 /w saturation
      ff10 = AE_SRAI64( ff10, 14 );
      ff11 = AE_SRAI64( ff11, 14 );

      // Q31 <- Q47 - 16 w/ rounding
      AE_S32X2RA64S_IP( ff10, ff11, pr );
    }

    //
    // Save sections' state.
    //

    AE_S32X2_IP( st0, state, +2*4 );
    AE_S32X2_IP( st1, state, +2*4 );

    // Second to last pair of sections are fed with output signal of the
    // previous pair.
    px = pr = (ae_int32x2*)( (uintptr_t)pr - N*4 );
  }

  //
  // Process the last section if the number of biquads is odd.
  //

  if ( M & 1 )
  {
    ae_int32x2 cf_gb2, cf_0b0, cf_b2b1, cf_a2a1;

    ae_int32x2 st0, x01;

    ae_int64 fb0, ff0; // fbY == Feedback, ffY == Feedforward
    ae_int64 fb1, ff1; // Y - sample number

    //
    // Load coefficients for the last section.
    //

    cf_gb2  = AE_L32X2_I( coef_gsos, 0*2*4 );
    cf_0b0  = AE_L32X2_I( coef_gsos, 1*2*4 );
    cf_b2b1 = AE_L32X2_I( coef_gsos, 2*2*4 );
    cf_a2a1 = AE_L32X2_I( coef_gsos, 3*2*4 );

    //
    // Load last section's state.
    //

    st0 = AE_L32X2_I( state, 0*2*4 );

    //
    // Pass N/2 sample pairs through the section.
    //

    __Pragma( "loop_count min=1" );
    for ( n=0; n<N/2; n++ )
    {
      AE_L32X2_IP( x01, px, +2*4 );

      // Q61 <- Q30*Q31
      fb0 = AE_MUL32_HH( cf_gb2, x01 );
      AE_MULSSD32_HH_LL( fb0, cf_a2a1, st0 );

      ff0 = AE_MULZAAD32_HH_LL( cf_b2b1, st0 );

      // Q31 <- Q61 + 2 - 32 w/ rounding, saturation
      AE_PKSRF32_( st0, fb0, 2 );

      AE_MULA32_LL( ff0, cf_0b0, st0 );

      fb1 = AE_MUL32_HL( cf_gb2, x01 );
      AE_MULSSD32_HH_LL( fb1, cf_a2a1, st0 );

      ff1 = AE_MULZAAD32_HH_LL( cf_b2b1, st0 );

      AE_PKSRF32_( st0, fb1, 2 );

      AE_MULA32_LL( ff1, cf_0b0, st0 );

      // Q47 <- Q61 - 14 /w saturation
      ff0 = AE_SRAI64( ff0, 14 );
      ff1 = AE_SRAI64( ff1, 14 );

      // Q31 <- Q47 - 16 w/ rounding
      AE_S32X2RA64S_IP( ff0, ff1, pr );
    }

    //
    // Save last section's state.
    //

    AE_S32X2_I( st0, state, 0 );
  }

  //
  // Scale output data, if required.
  //

  if ( bqriir->gain != 0 )
  {
    ae_int32x2 t0, t1;

    WUR_AE_SAR( bqriir->gain );

    px = (const ae_int32x2*)r;
    pr = (      ae_int32x2*)r;

    for ( n=0; n<N/4; n++ )
    {
      AE_L32X2_IP( t0, px, +2*4 );
      AE_L32X2_IP( t1, px, +2*4 );

      t0 = AE_SLAS32S( t0 );
      t1 = AE_SLAS32S( t1 );

      AE_S32X2_IP( t0, pr, +2*4 );
      AE_S32X2_IP( t1, pr, +2*4 );
    }

    if ( N & 2 )
    {
      t0 = AE_L32X2_I( px, 0 );

      t0 = AE_SLAS32S( t0 );

      AE_S32X2_I( t0, pr, 0 );
    }
  }

} // bqriir32x32_df2_process()
