/* iso646.h standard header */
#ifndef _ISO646
#define _ISO646

 #if !defined(__cplusplus) || defined(__GNUC__) && __GNUC__ < 3 \
	|| defined(__BORLANDC__)
  #define and	&&
  #define and_eq	&=
  #define bitand	&
  #define bitor	|
  #define compl	~
  #define not	!
  #define not_eq	!=
  #define or		||
  #define or_eq	|=
  #define xor	^
  #define xor_eq	^=
 #endif /* !defined(__cplusplus) || defined(__GNUC__) && __GNUC__ < 3 */

#endif /* _ISO646 */

