#ifndef __MPU_ERROR_H__
#define __MPU_ERROR_H__

enum MPU_ERROR_STATUS {
	MPU_OK = 0x0,

	/* MPU SVC Entry Errors (0x1000 - 0x1FFF) */
	MPU_ERROR_SVC_INVALID_ZONE_SIZE = 0x1000,
	MPU_ERROR_SVC_ZONE_ENTRY_NOT_FOUND = 0x1001,
	MPU_ERROR_SVC_ZONE_ENTRY_NOT_ACTIVE = 0x1002,

	/* MPU Dispatcher Errors (0x2000 - 0x2FFF)  */
	MPU_ERROR_DISP_UID_INVALID = 0x2000,
	MPU_ERROR_DISP_UID_UNINITED = 0x2001,
	MPU_ERROR_DISP_UID_DUPLICATED = 0x2002,
	MPU_ERROR_DISP_TYPE_INVALID = 0x2003,
	MPU_ERROR_DISP_ALIGNMENT = 0x2004,
	MPU_ERROR_DISP_REGION_INVALID = 0x2005,
	MPU_ERROR_DISP_REGION_LOCKED = 0x2006,
	MPU_ERROR_DISP_REGION_OVERLAPPED = 0x2007,
	MPU_ERROR_DISP_REGION_LOCK_NOT_SUPPORTED = 0x2008,
	MPU_ERROR_DISP_ORIGIN_REQUEST_NOT_ALLOWED = 0x2009,
	MPU_ERROR_DISP_FEAT_ID_INVALID = 0x200A,
	MPU_ERROR_DISP_FEAT_ENTRY_NOT_FOUND = 0x200B,
	MPU_ERROR_DISP_REE_REQUEST_PHY_ID_NOT_ALLOWED = 0x200C,
	MPU_ERROR_DISP_EMIMPU_PERM_SET_FAIL = 0x2010,
	MPU_ERROR_DISP_EMIMPU_PERM_CLR_FAIL = 0x2011,
	MPU_ERROR_DISP_EMIMPU_PHYS_REGION_ALREADY_OCCUPY = 0x2012,
	MPU_ERROR_DISP_DEVMPU_READ_PERM_SET_FAIL = 0x2020,
	MPU_ERROR_DISP_DEVMPU_READ_PERM_CLR_FAIL = 0x2021,
	MPU_ERROR_DISP_DEVMPU_WRITE_PERM_SET_FAIL = 0x2022,
	MPU_ERROR_DISP_DEVMPU_WRITE_PERM_CLR_FAIL = 0x2023,
	MPU_ERROR_DISP_DEVMPU_SET_OPERATION_NOT_SUPPORTED = 0x2024,
	MPU_ERROR_DISP_DEVMPU_CLEAR_OPERATION_NOT_SUPPORTED = 0x2025,
	MPU_ERROR_DISP_GENERIC = 0x2FFF,

	/* MPU Driver EMI MPU Errors (0x3000 - 0x3FFF)  */
	MPU_ERROR_DRV_EMIMPU_XXXX = 0x3000,

	/* MPU DRIVER DEVICE MPU Errors (0x4000 - 0x4FFF)  */
	MPU_ERROR_DRV_DEVMPU_XXXX = 0x4000,
};

#endif /* __MPU_ERROR_H__ */
